import React, { useEffect, useState } from "react";
import mapSvg from "./railsvgmap.svg";

export default function RailwayMap() {
  const [trains, setTrains] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await fetch("http://localhost:3001/api/map-layout");
        const data = await res.json();
        setTrains(data.layers.realtime || []);
      } catch (err) {
        console.error("Error fetching trains:", err);
      }
    }

    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  // SVG width from DevTools
  const svgWidth = 872;
  const svgHeight = 69;
  const totalDistanceKm = 25; // change to match max track distance in DB
  const scaleX = svgWidth / totalDistanceKm;

  return (
    <div style={{ textAlign: "center" }}>
      <h2>ðŸš† Railway Live Map</h2>
      <div style={{ position: "relative", display: "inline-block" }}>
        <img src={mapSvg} alt="Railway Map" style={{ width: svgWidth }} />
        {trains.map((t) => {
          const x = t.position_km * scaleX;
          const y = svgHeight / 2; // place train in middle of track
          return (
            <div
              key={t.id}
              style={{
                position: "absolute",
                left: `${x}px`,
                top: `${y}px`,
                width: "10px",
                height: "10px",
                borderRadius: "50%",
                background: "red",
                transform: "translate(-50%, -50%)",
              }}
              title={`Train ${t.train_id} (${t.speed_kmph} km/h)`}
            />
          );
        })}
      </div>
    </div>
  );
}
