import React, { useEffect, useState } from "react";
import map from "./railsvgmap.svg";

export default function RailwayMap() {
  const [trains, setTrains] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("http://localhost:3001/api/map-layout");
        const data = await res.json();

        console.log("Fetched trains:", data.layers.realtime); // ðŸ‘€ Debug
        setTrains(data.layers.realtime || []);
      } catch (err) {
        console.error("Error fetching trains:", err);
      }
    };

    fetchData(); // initial load
    const interval = setInterval(fetchData, 5000); // refresh every 5s

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ textAlign: "center" }}>
      <h2>ðŸš† Railway Live Map</h2>

      <svg width="100%" height="600">
        {/* Base SVG map */}
        <image href={map} width="100%" height="100%" />

        {/* Moving trains */}
        {trains.map((train, idx) => (
          <circle
            key={idx}
            cx={100 + train.position_km * 10} // X shifts with position
            cy={300}                          // adjust to your track Y
            r={6}
            fill="red"
          >
            <title>
              Train {train.train_id} â€” {train.position_km.toFixed(2)} km @{" "}
              {train.speed_kmph} km/h
            </title>
          </circle>
        ))}
      </svg>
    </div>
  );
}
