import React, { useEffect, useState } from "react";
import { ReactComponent as RailwaySVG } from "./railsvgmap.svg";

export default function RailwayMap() {
  const [trains, setTrains] = useState([]);

  // Fetch train positions from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch("http://localhost:3001/api/trains");
        const data = await res.json();
        setTrains(data);
      } catch (err) {
        console.error("Error fetching trains:", err);
      }
    };

    fetchData(); // initial
    const interval = setInterval(fetchData, 5000); // update every 5s
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ textAlign: "center" }}>
      <h2>ðŸš† Railway Live Map</h2>
      <div style={{ position: "relative", display: "inline-block", width: "100%" }}>
        {/* SVG Map */}
        <RailwaySVG style={{ width: "100%", height: "auto" }} />

        {/* Train Dots Overlay */}
        {trains.map((train) => (
          <div
            key={train.train_id}
            style={{
              position: "absolute",
              top: `${train.y}px`,   // TODO: adjust mapping logic
              left: `${train.x}px`,  // TODO: adjust mapping logic
              width: "10px",
              height: "10px",
              backgroundColor: "red",
              borderRadius: "50%",
              transform: "translate(-50%, -50%)",
            }}
            title={`Train ${train.train_id} @ ${train.position_km} km`}
          />
        ))}
      </div>
    </div>
  );
}
