import React, { useEffect, useState } from "react";
import svgMap from "./railsvgmap.svg";

const stationCoordinates = {
    JJP:  { km: 0,  x: 150,  y: 810 },   // Jaipur Junction
    BSGD: { km: 3,  x: 1060, y: 930 },   // Bais Godam
    DKBJ: { km: 6,  x: 2030, y: 810 },   // Dahar Ka Balaji
    GADJ: { km: 10, x: 2770, y: 820 },   // Gandhinagar Jaipur
    DPA:  { km: 15, x: 3800, y: 890 },   // Durgapura
    KNP:  { km: 18, x: 4420, y: 890 },   // Kanakpura
    GTJT: { km: 22, x: 4900, y: 900 },   // Getor Jagatpura
    NDH:  { km: 25, x: 5490, y: 820 },   // Nindhar Benar
    SNGN: { km: 30, x: 6180, y: 890 },   // Sanganer
    BDYK: { km: 35, x: 7430, y: 900 },   // Bindayaka
    KWP:  { km: 40, x: 8460, y: 880 },   // Khatipura
  };


export default function RailwayMap() {
  const [trains, setTrains] = useState([]);

  useEffect(() => {
    const fetchTrains = async () => {
      try {
        const res = await fetch("http://localhost:3001/api/trains");
        const data = await res.json();
        setTrains(data);
      } catch (err) {
        console.error("Error fetching trains:", err);
      }
    };

    fetchTrains();
    const interval = setInterval(fetchTrains, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ position: "relative" }}>
      <img src={svgMap} alt="Railway Map" style={{ width: "100%" }} />

      <svg
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          pointerEvents: "none",
        }}
      >
        {trains.map((train) => {
          const station = stationCoordinates[train.station_code]; // backend must provide station_code
          if (!station) return null;

          return (
            <circle
              key={train.train_id}
              cx={station.x / 15} // ðŸ”´ scale down to fit the resized SVG
              cy={station.y / 15}
              r={6}
              fill="red"
            >
              <title>
                {train.train_no} @ {train.station_code} â€“ {train.speed_kmph} km/h
              </title>
            </circle>
          );
        })}
      </svg>
    </div>
  );
}
