import React, { useEffect, useState } from "react";
import { ReactComponent as RailwaySVG } from "./assets/railsvgmap.svg";

export default function RailwayMap() {
  const [mapData, setMapData] = useState(null);

  // Fetch backend map data
  useEffect(() => {
    async function fetchData() {
      try {
        const res = await fetch("http://localhost:3001/api/map-layout");
        const data = await res.json();
        setMapData(data);
      } catch (err) {
        console.error("Error fetching map-layout:", err);
      }
    }
    fetchData();
    const interval = setInterval(fetchData, 5000); // refresh every 5s
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ position: "relative", width: "100%", height: "100vh" }}>
      {/* Base static SVG */}
      <RailwaySVG style={{ width: "100%", height: "100%" }} />

      {/* Overlay a new SVG for dynamic elements */}
      <svg
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          pointerEvents: "none", // allow clicks to pass through
        }}
      >
        {/* Draw tracks */}
        {stations.map((s, idx) => (
  <circle
    key={`station-${idx}`}
    cx={100 + Math.random() * 800}  // random visible position
    cy={100 + Math.random() * 200}
    r="6"
    fill="green"
  />
))}

{trains.map((t, idx) => (
  <circle
    key={`train-${idx}`}
    cx={200 + Math.random() * 600}
    cy={200 + Math.random() * 150}
    r="6"
    fill="red"
  />
))}

      </svg>
    </div>
  );
}
