// src/RailwayMap.jsx
import React, { useEffect, useState } from "react";
import railsvg from "./railsvgmap.svg";

export default function RailwayMap() {
  const [trains, setTrains] = useState([]);

  // Fetch trains from backend
  useEffect(() => {
    async function fetchData() {
      try {
        const res = await fetch("http://localhost:3001/api/map-layout");
        const data = await res.json();
        setTrains(data.layers.realtime || []);
      } catch (err) {
        console.error("Error fetching trains:", err);
      }
    }

    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  // Dummy position mapping (just to TEST if dots render)
  const mapToSvgCoords = (train, index) => {
    const x = 200 + index * 50; // shift horizontally
    const y = 300 + (index % 3) * 40; // stagger vertically
    return { x, y };
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h2>ðŸš† Railway Live Map</h2>

      {/* SVG Container */}
      <div style={{ position: "relative", display: "inline-block", width: "100%" }}>
        {/* Base SVG */}
        <img src={railsvg} alt="Railway Map" style={{ width: "100%", display: "block" }} />

        {/* Train overlays */}
        {trains.map((train, i) => {
          const { x, y } = mapToSvgCoords(train, i);
          return (
            <div
              key={train.id || i}
              style={{
                position: "absolute",
                top: `${y}px`,
                left: `${x}px`,
                width: "14px",
                height: "14px",
                borderRadius: "50%",
                backgroundColor: "red",
                border: "2px solid white",
                zIndex: 10, // ensure dot is above the SVG
                transform: "translate(-50%, -50%)",
              }}
              title={`Train ${train.train_id} | ${train.speed_kmph} km/h`}
            />
          );
        })}
      </div>
    </div>
  );
}
